    .form-submit {
		display: flex;
		align-items: center;
        justify-content: flex-end;
		position: relative;
        margin: 0 0 1rem auto;
	}

	.icon-box {
		display: flex;
		align-items: center;
		justify-content: center;
		position: relative;
		right: -0.6rem;
		background: var(--color-hva-pink);
		padding: 0.25rem;
		aspect-ratio: 1/1;
		transform: rotate(45deg);
		transition: transform 0.2s ease-in-out;
	}

	.icon-box > img {
		width: 0.8rem;
		height: 0.8rem;
		transform: rotate(-45deg);
	}

	a {
		padding: 0.4rem 0.9rem 0.4rem 1.3rem;
		border: none;
		background-color: var(--color-hva-navy);
		color: var(--color-white);
		font-size: 0.9rem;
		font-weight: 600;
		font-family: 'Open Sans', sans-serif;
		cursor: pointer;
	}

    a:hover {
		background-color: #362e61;
	}

    .form-submit:has(a:hover) .icon-box {
		transform: scale(1.4);
	}

	h1 {
		font: ExtraBold;
		font-size: 64px;
		margin-left: 70rem;
		color: #f25379;
	}
	h2 {
		margin-top: 10rem;
		margin-left: 34rem;
		font-size: 30px;
	}
	form {
		max-width: 60rem;
		margin: 0 auto;
		margin-top: 5rem;
		background-color: #1e1649;
		height: 50rem;
		padding: 2rem;
	}
	label {
		margin-top: 2rem;
		margin-bottom: 2rem;
		font: Extrabold;
	}
	#file {
		height: 10rem;
		background-color: #1e1649;
		border: solid 2 white;
		cursor: pointer;
	}
	label {
		display: block;
		margin-bottom: 10px;
	}

	input {
		width: 100%;
		padding: 8px;
		margin-top: 3px;
		margin-bottom: 10px;
		box-sizing: border-box;
		background-color: #1e1649;
		border: 2px solid #ffffff;
		color: #ffffff;
	}

	button {
		background-color: #1e1649;
		color: white;
		padding: 10px 15px;
		border: none;
		border-radius: 4px;
		cursor: pointer;
	}

	button:hover {
		background-color: #f25379;
	}
	.button-versturen {
		margin-left: 34rem;
		margin-top: 4rem;
		margin-bottom: 15rem;
		padding: 1rem 1rem;
	}

	@media only screen and (max-width: 50rem) {
		h1 {
			margin-left: 5rem;
			text-align: center;
			font: ExtraBold;
			font-size: 64px;
			color: #f25379;
			margin-top: 2rem;
		}

		h2 {
			margin-left: 0;
			text-align: center;
		}

		form {
			max-width: 30rem;

			margin-top: 5rem;
			background-color: #1e1649;
			height: auto;
			padding: 2rem;
		}

		label {
			margin-top: 1rem;
			margin-bottom: 1rem;
			font-weight: bold;
		}

		input {
			width: 100%;
			padding: 8px;
			margin-top: 3px;
			margin-bottom: 10px;
			box-sizing: border-box;
			background-color: #1e1649;
			border: 2px solid #ffffff;
			color: #ffffff;
		}

		#file {
			height: auto;
		}

		.button-versturen {
			margin-left: 2rem;
			margin-top: 2rem;
			margin-bottom: 2rem;
			font-size: large;
			padding: 1rem 1rem;
		}

		.titel {
			text-align: center;
		}
	}

